package main

import (
	"Lab/practic2/lib"
	"log"
)

func main() {
	hexKey := ""
	size := 512
	partOfKey := lib.SearchingValue(hexKey)
	//log.Println("search", partOfKey)
	littleEnd := lib.ConvertHexToDec(partOfKey)
	log.Println("little endian ", littleEnd)
	bigEnd := lib.ConvertHexToDec(hexKey)
	log.Println("big endian ", bigEnd)
	bigToHex := lib.ConvertToHex(bigEnd, size)
	log.Println("big to hex ", bigToHex)
	littleToHex := lib.ConvertToHex(littleEnd, size)
	log.Println("little to hex ", littleToHex)
}
